package me.zivush.customfishingz;

import org.bukkit.*;
import org.bukkit.boss.BarColor;
import org.bukkit.boss.BarStyle;
import org.bukkit.boss.BossBar;
import org.bukkit.configuration.ConfigurationSection;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.enchantments.Enchantment;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.player.PlayerFishEvent;
import org.bukkit.event.player.PlayerInteractEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.plugin.java.JavaPlugin;
import org.bukkit.scheduler.BukkitRunnable;

import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Random;

public class CustomFishingZ extends JavaPlugin implements Listener {

    private Location poolLocation;
    private int poolRadius;
    private Map&lt;String, Reward&gt; rewards;
    private Map&lt;Player, FishingGame&gt; activeGames;
    private String fishEscapeMessage;
    private String fishCaughtMessage;
    private boolean randomSpeed;
    private int fixedSpeed;
    private int minSpeed;
    private int maxSpeed;

    @Override
    public void onEnable() {
        saveDefaultConfig();
        loadConfig();
        getServer().getPluginManager().registerEvents(this, this);
        CustomFishingZCommands commands = new CustomFishingZCommands(this);
        getCommand(&quot;customfishingz&quot;).setExecutor(commands);
        getCommand(&quot;customfishingz&quot;).setTabCompleter(commands);
        activeGames = new HashMap&lt;&gt;();
    }

    @Override
    public void onDisable() {
        for (FishingGame game : activeGames.values()) {
            game.end();
        }
    }

    public void loadConfig() {
        reloadConfig();
        FileConfiguration config = getConfig();

        String[] poolLoc = config.getString(&quot;pool.location&quot;, &quot;0, 64, 0, world&quot;).split(&quot;, &quot;);
        World world = getServer().getWorld(poolLoc[3]);
        if (world == null) {
            getLogger().warning(&quot;Invalid world name in config. Using default world.&quot;);
            world = getServer().getWorlds().get(0);
        }
        poolLocation = new Location(world, Double.parseDouble(poolLoc[0]), Double.parseDouble(poolLoc[1]), Double.parseDouble(poolLoc[2]));
        poolRadius = config.getInt(&quot;pool.radius&quot;, 15);

        rewards = new HashMap&lt;&gt;();
        ConfigurationSection rewardsSection = config.getConfigurationSection(&quot;rewards&quot;);
        if (rewardsSection != null) {
            for (String key : rewardsSection.getKeys(false)) {
                ConfigurationSection rewardSection = rewardsSection.getConfigurationSection(key);
                if (rewardSection != null) {
                    rewards.put(key, new Reward(
                            rewardSection.getString(&quot;message&quot;, &quot;You won a reward!&quot;).replace('&amp;', '§'),
                            rewardSection.getStringList(&quot;commands&quot;),
                            rewardSection.getDouble(&quot;chance&quot;, 0.1)
                    ));
                }
            }
        }

        fishEscapeMessage = ChatColor.translateAlternateColorCodes('&amp;', config.getString(&quot;messages.fish_escape&quot;, &quot;&amp;cYou did not catch the fish&quot;));
        fishCaughtMessage = ChatColor.translateAlternateColorCodes('&amp;', config.getString(&quot;messages.fish_caught&quot;, &quot;&amp;aYou caught the fish&quot;));

        randomSpeed = config.getBoolean(&quot;settings.random_speed&quot;, false);
        fixedSpeed = config.getInt(&quot;settings.speed&quot;, 1);
        minSpeed = config.getInt(&quot;settings.min_speed&quot;, 1);
        maxSpeed = config.getInt(&quot;settings.max_speed&quot;, 4);
    }

    @EventHandler
    public void onPlayerFish(PlayerFishEvent event) {
        Player player = event.getPlayer();
        if (event.getState() == PlayerFishEvent.State.CAUGHT_FISH) {
            Location fishLocation = event.getHook().getLocation();
            if (isInPool(fishLocation)) {
                event.setCancelled(true);
                ItemStack fishingRod = player.getInventory().getItemInMainHand();
                if (fishingRod.getType() == Material.FISHING_ROD) {
                    int unbreakingLevel = fishingRod.getEnchantmentLevel(Enchantment.DURABILITY);
                    boolean shouldTakeDamage = true;

                    if (unbreakingLevel &gt; 0) {
                        Random random = new Random();
                        int chance = 100 / (unbreakingLevel + 1);
                        shouldTakeDamage = random.nextInt(100) &lt; chance;
                    }

                    if (shouldTakeDamage) {
                        fishingRod.setDurability((short) (fishingRod.getDurability() + 1));
                    }
                }
                Bukkit.getScheduler().scheduleSyncDelayedTask(this, new Runnable() {
                    @Override
                    public void run() {
                        startFishingGame(player);
                    }
                }, 3L);
            }
        }
    }

    @EventHandler
    public void onPlayerInteract(PlayerInteractEvent event) {
        Player player = event.getPlayer();
        FishingGame game = activeGames.get(player);
        if (game != null) {
            game.onInteract();
        }
    }

    private boolean isInPool(Location location) {
        if (location == null || location.getWorld() == null || poolLocation == null || poolLocation.getWorld() == null) {
            getLogger().warning(&quot;Null location or world detected in isInPool check.&quot;);
            return false;
        }
        if (!location.getWorld().equals(poolLocation.getWorld())) {
            return false;
        }
        return location.distanceSquared(poolLocation) &lt;= poolRadius * poolRadius;
    }

    private void startFishingGame(Player player) {
        if (activeGames.containsKey(player)) {
            activeGames.get(player).end();
        } else {
            FishingGame game = new FishingGame(player);
            activeGames.put(player, game);
            game.start();
        }
    }

    private void giveReward(Player player) {
        double random = Math.random();
        double cumulativeChance = 0;

        for (Reward reward : rewards.values()) {
            cumulativeChance += reward.getChance();
            if (random &lt;= cumulativeChance) {
                player.sendMessage(reward.getMessage());
                for (String command : reward.getCommands()) {
                    command = command.replace(&quot;%player%&quot;, player.getName());
                    Bukkit.dispatchCommand(Bukkit.getConsoleSender(), command);
                }
                return;
            }
        }
    }

    public void setPool(Location location, int radius) {
        if (location == null || location.getWorld() == null) {
            getLogger().warning(&quot;Attempted to set pool with null location or world.&quot;);
            return;
        }
        poolLocation = location;
        poolRadius = radius;
        FileConfiguration config = getConfig();
        config.set(&quot;pool.location&quot;, String.format(&quot;%.2f, %.2f, %.2f, %s&quot;, location.getX(), location.getY(), location.getZ(), location.getWorld().getName()));
        config.set(&quot;pool.radius&quot;, radius);
        saveConfig();
    }

    private class FishingGame {
        private final Player player;
        private final BossBar bossBar;
        private BukkitRunnable gameTask;
        private int position;
        private int count;
        private boolean increasing;
        private int gameSpeed;

        public FishingGame(Player player) {
            this.player = player;
            this.bossBar = Bukkit.createBossBar(&quot;§7█████████§2███§7█████████&quot;, BarColor.GREEN, BarStyle.SOLID);
            this.bossBar.addPlayer(player);

            if (randomSpeed) {
                Random random = new Random();
                gameSpeed = random.nextInt(maxSpeed - minSpeed + 1) + minSpeed;
            } else {
                gameSpeed = fixedSpeed;
            }
        }

        public void start() {
            position = 0;
            increasing = true;
            gameTask = new BukkitRunnable() {
                @Override
                public void run() {
                    if (count &gt;= 80) {
                        end();
                        player.sendMessage(fishEscapeMessage);
                        player.playSound(player.getLocation(), Sound.ENTITY_FISH_SWIM, 1.0f, 1.0f);
                    }
                    if (increasing) {
                        position++;
                        count++;
                        if (position &gt;= 20) {
                            increasing = false;
                        }
                    } else {
                        position--;
                        count++;
                        if (position &lt;= 0) {
                            increasing = true;
                        }
                    }
                    updateBossBar();
                }
            };
            gameTask.runTaskTimer(CustomFishingZ.this, 0, gameSpeed);
        }

        public void onInteract() {
            if ((position == 9 || position == 10 || position == 11)) {
                end();
                giveReward(player);
                player.sendMessage(fishCaughtMessage);
                player.playSound(player.getLocation(), Sound.ENTITY_EXPERIENCE_ORB_PICKUP, 1.0f, 1.0f);
            } else {
                end();
                player.sendMessage(fishEscapeMessage);
                player.playSound(player.getLocation(), Sound.ENTITY_FISH_SWIM, 1.0f, 1.0f);
            }
        }

        public void end() {
            if (gameTask != null) {
                gameTask.cancel();
            }
            player.getWorld().getEntities().stream()
                    .filter(entity -&gt; entity instanceof org.bukkit.entity.FishHook)
                    .map(entity -&gt; (org.bukkit.entity.FishHook) entity)
                    .filter(fishHook -&gt; fishHook.getShooter() instanceof Player)
                    .filter(fishHook -&gt; fishHook.getShooter().equals(player))
                    .forEach(org.bukkit.entity.FishHook::remove);

            bossBar.removeAll();
            activeGames.remove(player);
        }

        private void updateBossBar() {
            StringBuilder barText = new StringBuilder(&quot;§7&quot;);
            for (int i = 0; i &lt; 21; i++) {
                if (i == position) {
                    barText.append(&quot;§a█&quot;);
                } else if (i == 9 || i == 10 || i == 11) {
                    barText.append(&quot;§2█&quot;);
                } else {
                    barText.append(&quot;§7█&quot;);
                }
            }
            bossBar.setTitle(barText.toString());
        }
    }

    private static class Reward {
        private final String message;
        private final List&lt;String&gt; commands;
        private final double chance;

        public Reward(String message, List&lt;String&gt; commands, double chance) {
            this.message = message;
            this.commands = commands;
            this.chance = chance;
        }

        public String getMessage() {
            return message;
        }

        public List&lt;String&gt; getCommands() {
            return commands;
        }

        public double getChance() {
            return chance;
        }
    }
}

